mapper.select:
SELECT student.studentID,student.studentName FROM t_class_base_info class JOIN t_student_base_info student ON class.classID = student.classID
AND class.className = #{className}
AND class.majorID = #{majorID}

SELECT
    className
  FROM
    t_class_base_info


        AND  majorID = #{majorID}


        AND  grade = #{grade}

SELECT
    teacherNum
  FROM
    t_teacher_base_info


           AND  majorID = #{majorID}


            AND teacherName = #{teacherName}

SELECT
    ia.courseDesignArrangeID
  FROM
      t_courseDesign_info_arrange ia
      JOIN t_courseDesign_class_arrange ca
        ON ca.courseDesignArrangeID = ia.courseDesignArrangeID
    WHERE
      ia.trainCourseID = #{trainCourseID}
        AND ia.yearNum = #{yearNum}
        AND ia.majorID = #{majorID}
        AND ca.grade = #{grade}
