mapper.select:
select yearNum,semesterNum,teacherTitleID,titlename,teacherName,titleOrigin,majorName,reqireStudentNum,checkStatus,isSubmit from gradeuateBaseInfo g,t_major_base_info m, ( select teacherTitleID,titlename,titleOrigin,reqireStudentNum,checkStatus,teacherName,t2.majorID,graDesignID,isSubmit from teacherGreDesignTitle t1,t_teacher_base_info t2 where t1.teacherID=t2.teacherID
and t1.teacherID like #{teacherID}
and t2.teacherName like '${teacherName}%'
and t1.titlename like '${titlename}%'
and t1.checkStatus like '${checkStatus}'
) son where m.majorID = son.majorID and g.graDesignID = son.graDesignID and g.yearNum = #{yearNum}

