mapper.select:
select yearNum,semesterNum,teacherTitleID,titlename,teacherName,titleOrigin,majorName,reqireStudentNum,checkStatus from gradeuateBaseInfo g,t_major_base_info m ( select teacherTitleID,titlename,titleOrigin,reqireStudentNum,checkStatus,teacherName,majorID,graDesignID from teacherGreDesignTitle t1,t_teacher_base_info t2 where t1.teacherID=#{teacherID} and t2.teacherID=#{teacherID}
and t2.teacherName like '${teacherName}%'
and t1.titlename like '${titlename}%'
and t1.checkStatus like '${checkStatus}'
) son where m.majorID = son.majorID and g.graDesignID = son.graDesignID and g.yearNum = #{yearNum}

