mapper.select:
SELECT * from textbookorderinfo



        and remark=#{major}


        and yearNum like '%${year}%'


        and semester=#{term}


        and isOrder=#{isOrder}


        and isCheck=#{isCheck}

SELECT t1.arrange_course_id,t1.course_name,t1.course_code, (SELECT GROUP_CONCAT(teacher_name) FROM ap_teacher_course t2 WHERE t1.arrange_course_id=t2.arrange_course_id)teacher_name, (SELECT GROUP_CONCAT(teacher_number) FROM ap_teacher_course t2 WHERE t1.arrange_course_id=t2.arrange_course_id)teacher_number, t1.class_name,t1.major_students_number FROM ap_task_arrange_course t1,ap_arrange_course_task t3,textbookorderinfo t4
t1.arrange_task_id=t3.arrange_task_id AND t3.major_name=t4.roomName AND t3.academic_year=t4.yearNum AND t3.term=t4.semester AND t3.task_receipt_name=t4.createPerson AND t4.orderid=#{orderId}
ORDER BY CONVERT (t1.course_name USING gbk)

SELECT t1.arrange_course_id,t1.course_name,t1.course_code, (SELECT GROUP_CONCAT(teacher_name) FROM ap_teacher_course t2 WHERE t1.arrange_course_id=t2.arrange_course_id)teacher_name, (SELECT GROUP_CONCAT(teacher_number) FROM ap_teacher_course t2 WHERE t1.arrange_course_id=t2.arrange_course_id)teacher_number, t1.class_name,t1.major_students_number,t3.isOrder,t3.isConfirm FROM ap_task_arrange_course t1,textbook_order t3
t1.arrange_course_id=t3.orderDetailID AND t3.orderid=#{orderId}
and t1.course_code=#{course}
and t3.isOrder=#{isOrder}
and t3.isConfirm=#{isConfirm}
ORDER BY CONVERT (t1.course_name USING gbk)

SELECT * FROM t_course_base_info t1,t_textbook_base_info t2
WHERE t1.courseId=t2.courseID AND t1.courseNum=#{courseCode}
ORDER BY CONVERT (t2.textbookName USING gbk)

SELECT t1.course_name,t1.course_code, (SELECT GROUP_CONCAT(teacher_name) FROM ap_teacher_course t2 WHERE t1.arrange_course_id=t2.arrange_course_id)teacher_name, (SELECT GROUP_CONCAT(teacher_number) FROM ap_teacher_course t2 WHERE t1.arrange_course_id=t2.arrange_course_id)teacher_number, t1.class_name,t1.major_students_number,t3.*,t5.* FROM ap_task_arrange_course t1,ap_teacher_course t2,textbook_order t3,textbookorderinfo t4,t_textbook_base_info t5
t1.arrange_course_id=t3.orderDetailID AND t1.arrange_course_id=t2.arrange_course_id AND t3.textbookID=t5.textbookID AND t3.orderid=t4.orderid
and t2.teacher_number=#{userCode}
and t4.remark=#{major}
and t4.yearNum like '%${year}%'
and t4.semester=#{term}
and t3.isConfirm=#{isConfirm}

SELECT t1.course_name,t1.course_code, (SELECT GROUP_CONCAT(teacher_name) FROM ap_teacher_course t2 WHERE t1.arrange_course_id=t2.arrange_course_id)teacher_name, (SELECT GROUP_CONCAT(teacher_number) FROM ap_teacher_course t2 WHERE t1.arrange_course_id=t2.arrange_course_id)teacher_number, t1.class_name,t1.major_students_number,t3.*,t5.* FROM ap_task_arrange_course t1,textbook_order t3,textbookorderinfo t4,t_textbook_base_info t5
t1.arrange_course_id=t3.orderDetailID AND t3.textbookID=t5.textbookID AND t3.orderid=t4.orderid AND t4.orderid=#{OrderID}
and t1.course_code=#{course}
and t3.isConfirm=#{isConfirm}
